function $_GET(q,s) {
    s = (s) ? s : self.location.search;
    var re = new RegExp('&'+q+'=([^&]*)','i');
    return (s=s.replace(/^\?/,'&').match(re)) ? s=s[1] : s='';
}

function backToList(){
  //alert($_GET('PRO_UID'));
  self.location = 'cases_Scheduler_List?PRO_UID='+$_GET('PRO_UID');
}
