dynaForm.JS:
var verifyFile = function(oForm) {
    if (document.getElementById('form[PLUGIN_FILENAME]').value != '') {
      oForm.submit();
    }
    else {
      msgBox('@G::LoadTranslation(ID_PLEASE_SELECT_PLUGIN)', 'alert');
    }
  };

function cancel(){
    window.location = 'pluginsList';
}
