/**
 * Failsafe remove an element from a collection
 *
 * @param  {Array<Object>} [collection]
 * @param  {Object} [element]
 *
 * @return {Object} the element that got removed or undefined
 */
var CollectionRemove = function(collection, element) {

    if (!collection || !element) {
        return;
    }

    var idx = collection.indexOf(element);
    if (idx === -1) {
        return;
    }

    collection.splice(idx, 1);

    return element;
};

function setEncoded(link, name, data) {
    var encodedData = encodeURIComponent(data);

    if (window.navigator.msSaveBlob) {
        window.navigator.msSaveBlob(new Blob([data],{type: "application/octet-stream"}), name);

    } else {
        if (data) {
            link.addClass('active').attr({
                'href': 'data:application/bpmn20-xml;charset=UTF-8,' + encodedData,
                'download': name
            });
        } else {
            link.removeClass('active');
        }
    }

}

function convertDatetimeToIso8601(datetime) {
    var separate = datetime.split(' ');
    var date = separate[0].split('-');
    var time = (separate[1] || '00:00:00').split(':');
    var date = new Date(date[0] || 0, (date[1] || 1) - 1, date[2] || 0, time[0] || 0, time[1] || 0, time[2] || 0);
    var timeDifference = date.getTimezoneOffset();
    var signed = '+';
    if (timeDifference > 0) {
        signed = '-';
    }
    timeDifference = Math.abs(timeDifference);
    var hours = Math.floor(timeDifference / 60).toString();
    var minutes = Math.floor(timeDifference % 60).toString();
    return datetime.replace(/\s/g, 'T') + signed +
            ('0' + hours).slice(-2) + ':' + ('0' + minutes).slice(-2);
}


