        Y2 = this.endPoint.y;
        px = point.x;
        py = point.y

        X2 -= X1;
        Y2 -= Y1;

        px -= X1;
        py -= Y1;

        dotprod = px * X2 + py * Y2;
        if (dotprod <= 0.0) {
            projlenSq = 0.0;
        } else {
            px = X2 - px;
            py = Y2 - py;
            dotprod = px * X2 + py * Y2;
            if (dotprod <= 0.0) {
                projlenSq = 0.0;
            } else {
                projlenSq = dotprod * dotprod / (X2 * X2 + Y2 * Y2);
            }
        }

        var lenSq = px * px + py * py - projlenSq;
